import React, { useState } from "react";
import Cell from "./cell";

const Table = () => {
    let bombs = [];
    const initBombs = () => {
        bombs = [];
        // init random bombs positions
        let num = 10;
        for (let h = 0; h < num; h++) {
            let bombX = Math.floor(Math.random() * 9);
            let bombY = Math.floor(Math.random() * 9);
            if (bombs.find(el => el.x === bombX && el.y === bombY)) {
                num += 1;
            } else {
                bombs.push({ x: bombX, y: bombY });
            }
        }
    };

    const [first, setClick] = useState(true);
    // function to handle click on cases
    const handleClick = el => {
        let arr = [...state];

        if (first) {
            firstClick();
            setClick(false);
        }
        console.log(el.x, el.y);

        // handle end of game
        if (arr[el.x][el.y].bombs) {
            winner();
            setMessage(`You lost!!! You found ${found} bomb(s)`);
            arr.map(el => el.map(e => (e.show = true)));
            setState(arr);
        }
        // handle empty cell with callback
        else if (arr[el.x][el.y].show === false && arr[el.x][el.y].count === 0) {
            arr[el.x][el.y].show = true;
            toggleShow(el.x, el.y);
            // handle cell with count > 0 without callback
        } else if (arr[el.x][el.y].show === false && arr[el.x][el.y].count > 0) {
            arr[el.x][el.y].show = true;
            setState(arr);
        }
    };
    const toggleShow = (a, b) => {
        if (a > 0) {
            check(a - 1, b);
        }
        if (b > 0) {
            check(a, b - 1);
        }
        if (b < 8) {
            check(a, b + 1);
        }
        if (a < 8) {
            check(a + 1, b);
        }
    };
    const check = (a, b) => {
        let arr = [...state];
        if (arr[a][b].count === 0 && arr[a][b].show === false) {
            arr[a][b].show = true;
            return toggleShow(a, b);
        } else {
            console.log(arr);
            arr[a][b].show = true;
            setState(arr);
        }
    };

    // init board with the bombs

    let row = [];
    let myResult = [];
    let result = {};
    for (let i = 0; i < 9; i++) {
        for (let j = 0; j < 9; j++) {
            if (bombs.find(el => el.x === i && el.y === j)) {
                result = {
                    x: i,
                    y: j,
                    bombs: true,
                    count: 0,
                    show: false,
                    mark: false
                };
                row.push(result);
            } else {
                result = {
                    x: i,
                    y: j,
                    bombs: false,
                    count: 0,
                    show: false,
                    mark: false
                };
                row.push(result);
            }
        }
        myResult.push(row);
        row = [];
    }
    const init = myResult;

    const [state, setState] = useState(init);
    const [counter, setCounter] = useState(0);
    let initMessage = "Right click to mark bombs position";
    const [message, setMessage] = useState(initMessage);

    const counting = (a, b) => {
        let arr = [...state];
        if (a > 0) {
            arr[a - 1][b].count += 1;
        }
        if (a > 0 && b > 0) {
            arr[a - 1][b - 1].count += 1;
        }
        if (b > 0) {
            arr[a][b - 1].count += 1;
        }
        if (a > 0 && b < 8) {
            arr[a - 1][b + 1].count += 1;
        }
        if (b < 8) {
            arr[a][b + 1].count += 1;
        }
        if (a < 8) {
            arr[a + 1][b].count += 1;
        }
        if (a < 8 && b > 0) {
            arr[a + 1][b - 1].count += 1;
        }
        if (a < 8 && b < 8) {
            arr[a + 1][b + 1].count += 1;
        }
    };
    const firstClick = () => {
        initBombs();
        let arr = [...state];
        bombs.map(el => (arr[el.x][el.y].bombs = true));
        bombs.map(el => counting(el.x, el.y));
    };
    const rightClick = (e, el) => {
        e.preventDefault();
        let arr = [...state];
        const checked = el.mark;
        el.mark = !checked;
        if (el.mark) {
            setCounter(counter + 1);
        } else {
            setCounter(counter - 1);
        }
        winner();
        setState(arr);
    };
    const [found, setFound] = useState(0);
    const winner = () => {
        let num = 0;
        state.map(elem =>
            elem.map(el => (el.bombs && el.mark ? (num += 1) : null))
        );
        setFound(num);
        if (num === 10) {
            setMessage(`You WON!!! Congrats`);
            let arr = [...state];
            arr.map(el => el.map(e => (e.show = true)));
            setState(arr);
        }
    };
    return (
        <>
            <div className="title">MINESWEEPER GAME</div>
            <div className="title">Find the 10 bombs</div>
            <div className="title">You marked: {counter} Bombs</div>
            <ul>
                {state.map((el, index) => {
                    return (
                        <li className="row" key={index}>
                            {el.map((elem, i) => {
                                return (
                                    <Cell
                                        key={i}
                                        ind={i}
                                        show={elem.show}
                                        checked={elem.mark}
                                        count={elem.show ? elem.count : null}
                                        bomb={elem.bombs}
                                        right={e => {
                                            rightClick(e, elem);
                                        }}
                                        action={() => {
                                            handleClick(elem);
                                        }}
                                    />
                                );
                            })}
                        </li>
                    );
                })}
            </ul>
            <div className="message">{message}</div>
            <button onClick={() => window.location.reload()}>new game</button>

            {/* <a href="https://github.com/Stephanie-Merle/minesweeper">
        ⭐️ CHECK SOURCE CODE HERE ⭐️
      </a> */}
        </>
    );
};

export default Table;
